import React  from "react";
import AmazonLogo from "../images/amazon_logo.png";

function LoginComponent() {
    return (
        <div style={{ justifyContent:'center', alignItems:'center', display:'flex', flexDirection:'column', }}>
            <img src={AmazonLogo} alt="Amazon Logo" style={{ width: '130px', height: '100px' }}/>
            <div style={{width: '400px',textAlign:'left',border: '2px solid rgba(0,0,0,0.2)', padding: '25px', margin: '5px', borderRadius: '10px', backgroundColor: 'white'}}>                
                <h2>Sign in or Create an Account</h2>
                <p>Enter mobile number or email</p>
                <textarea placeholder="" rows="1" cols="30"></textarea>
                <br></br>
                <p>Password</p>
                <input type="password" placeholder="" style={{ width: '80%', padding: '10px', border: '1px solid rgba(0,0,0,0.2)', borderRadius: '5px' }}></input>
                <br></br>
                <button style={{ width: '100%', backgroundColor: '#FF9800', color: 'white', padding: '12px 20px', border: 'none', borderRadius: '5px', marginTop:'10px'}}>Sign In</button>
                <br></br>
            </div>
        </div>
    );
}

export { LoginComponent };
