import React, { useState } from "react";
import AmazonLogo from "../images/amazon_logo.png";
import { useNavigate } from "react-router-dom";
import { CreateAccount } from "../Components/createAccountPage.js";

function LoginComponent() {
    const [showCreateAccount, setCreateAccount] = useState(false);
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const handleLogin = async () => {
        const res = await fetch('http://localhost:5000/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                username,
                password
            })
        });

        const data = await res.json();
        console.log(data);
        // You can navigate to another page on success:
        // if (data.success) navigate("/somewhere");
    };

    if (showCreateAccount) {
        return <CreateAccount />;
    }

    return (
        <div style={{ justifyContent:'center', alignItems:'center', display:'flex', flexDirection:'column' }}>
            <img src={AmazonLogo} alt="Amazon Logo" style={{ width: '200px', height: '80px' }}/>
            <div style={{width: '400px', textAlign:'left', border: '2px solid rgba(0,0,0,0.2)', padding: '35px', margin: '5px', borderRadius: '10px', backgroundColor: 'white'}}>                
                <h2>Sign in or Create an Account</h2>
                <p>Enter mobile number or email</p>
                <input
                    id="username"
                    type="text"
                    value={username}
                    onChange={e => setUsername(e.target.value)}
                    style={{ width: '95%', padding: '12px', border: '1px solid rgba(0,0,0,0.2)', borderRadius: '5px' }}
                />
                <br />
                <p>Password</p>
                <input
                    id="password"
                    type="password"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    style={{ width: '95%', padding: '12px', border: '1px solid rgba(0,0,0,0.2)', borderRadius: '5px' }}
                />
                <br />
                <button
                    style={{ width: '102%', backgroundColor: '#FF9800', color: 'Black', padding: '14px 20px', border: 'none', borderRadius: '25px', marginTop:'10px'}}
                    onClick={handleLogin}
                >
                    Sign In
                </button>
                <br />
                <br />
                <a href="#help" style={{ textDecoration: 'none', color: 'blue' }}>Need help?</a> 
                <br />
                <hr style={{ border: '1px solid rgba(0,0,0,0.2)' }} />
                <span
                    style={{ textDecoration: 'none', color: 'blue', cursor: 'pointer' }}
                    onClick={() => setCreateAccount(true)}
                >
                    Create your Amazon account
                </span>
                <br />
            </div>
        </div>
    );
}

export { LoginComponent };
