import React  from "react";
import AmazonLogo from "../images/amazon_logo.png";

function LoginComponent() {

    const handleLogin = async () => {
        const res = await fetch('http://localhost:5000/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                username: document.getElementById("username").value,
                password: document.getElementById("password").value
            })
        });

        const data = await res.json();
        console.log(data);
    };


    return (
        <div style={{ justifyContent:'center', alignItems:'center', display:'flex', flexDirection:'column', }}>
            <img src={AmazonLogo} alt="Amazon Logo" style={{ width: '200px', height: '80px' }}/>
            <div style={{width: '400px',textAlign:'left',border: '2px solid rgba(0,0,0,0.2)', padding: '15px', margin: '5px', borderRadius: '10px', backgroundColor: 'white'}}>                
                <h2>Sign in or Create an Account</h2>
                <p>Enter mobile number or email</p>
                <input id="username" type="username" placeholder="" style={{ width: '80%', padding: '10px', border: '1px solid rgba(0,0,0,0.2)', borderRadius: '5px' }}></input>
                <br></br>
                <p>Password</p>
                <input id="password" type="password" placeholder="" style={{ width: '80%',padding: '10px', border: '1px solid rgba(0,0,0,0.2)', borderRadius: '5px' }}></input>
                <br></br>
                <button style={{ width: '100%', backgroundColor: '#FF9800', color: 'Black', padding: '12px 20px', border: 'none', borderRadius: '25px', marginTop:'10px'}} onClick={handleLogin}>Sign In</button>
                <br></br>
                <br></br>
                <a href="#" style={{ textDecoration: 'none', color: 'blue' }}>Need help?</a> 
                <br></br>
                {/* <br></br> */}
                <a href="#" style={{ textDecoration: 'none', color: 'blue' }}>Create your Amazon account</a>
                <br></br>
            </div>
        </div>
    );
}

export { LoginComponent };
