import React from 'react';
import { useEffect,useState } from 'react';
import '../CSS/HomePage.css';

function Home() {
    const [backgroundColor, setBackgroundColor] = useState('white');
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8383/api/products') 
            .then(res => res.json())
            .then(data => setProducts(data))
            .catch(err => console.error("Fetch error:", err));
    })

    const handleSearch = () => {
        setBackgroundColor(backgroundColor === 'white' ? 'lightblue' : 'white');
    };

    return (
        <div className='Homae-Main-Container'>
            {/* top panel */}
            <div className='home-dashboard'>
                <input type='text' placeholder='Search' />
                <button className="home-button-search" onClick={handleSearch}>Go</button>
            </div>

            <div className="homepage">
                <h2>Recommended Products</h2>
                <div className="product-grid">
                {products.map((p) => (
                    <div className="product-card" key={p.asin}>
                    <img src={p.image_url || '/fallback.jpg'} alt={p.title} />
                    <h4>{p.title}</h4>
                    <p>Price: ${p.price}</p>
                    <p>Rating: {p.rating} ‚≠ê</p>
                    </div>
                ))}
                </div>
            </div>

        </div>
    );
}

export {Home} ;
